import React, { useState, useEffect } from "react";
import "../styles/Login.css";
import { FaUser, FaEnvelope, FaLock } from "react-icons/fa";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [remember, setRemember] = useState(false);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });
      const data = await res.json();
      if (data.success) {
        alert("✅ Connexion réussie !");
        localStorage.setItem("token", data.token);
      } else {
        alert("❌ Identifiants invalides");
      }
    } catch (error) {
      console.error("Erreur:", error);
    }
  };

  // Générer des positions aléatoires pour les pixels (beaucoup plus)
  const generateDataPixels = (count, side) => {
    return Array.from({ length: count }).map((_, i) => ({
      id: i,
      top: `${Math.random() * 90 + 5}%`,
      left: side === 'left' ? `${Math.random() * 70}%` : `${Math.random() * 70 + 30}%`,
      delay: `${Math.random() * 5}s`,
      duration: `${Math.random() * 4 + 3}s`,
      size: Math.random() > 0.5 ? 'large' : Math.random() > 0.5 ? 'small' : 'normal'
    }));
  };

  const leftPixels = generateDataPixels(80, 'left');
  const rightPixels = generateDataPixels(80, 'right');

  // Générer des barres d'égaliseur multiples
  const generateEqualizerBars = (count) => {
    return Array.from({ length: count }).map((_, i) => ({
      id: i,
      height: `${Math.floor(Math.random() * 50 + 20)}px`,
      delay: `${Math.random() * 2}s`,
      duration: `${Math.random() * 1 + 0.8}s`
    }));
  };

  const leftBars1 = generateEqualizerBars(20);
  const leftBars2 = generateEqualizerBars(15);
  const leftBars3 = generateEqualizerBars(18);
  const rightBars1 = generateEqualizerBars(22);
  const rightBars2 = generateEqualizerBars(16);
  const rightBars3 = generateEqualizerBars(19);

  // Générer des codes-barres
  const generateBarcodes = (count) => {
    return Array.from({ length: count }).map((_, i) => ({
      id: i,
      height: `${Math.floor(Math.random() * 60 + 20)}px`,
      width: `${Math.floor(Math.random() * 3 + 2)}px`,
      delay: `${Math.random() * 3}s`
    }));
  };

  const barcodeLeft1 = generateBarcodes(12);
  const barcodeLeft2 = generateBarcodes(15);
  const barcodeRight1 = generateBarcodes(12);
  const barcodeRight2 = generateBarcodes(15);

  return (
    <div className="login-container">
      {/* GRILLES MULTIPLES */}
      <div className="grid-secondary"></div>
      <div className="grid-diagonal"></div>
      
      {/* LIGNES DE BALAYAGE */}
      <div className="scan-line"></div>
      <div className="scan-line-2"></div>

      {/* ========== CIRCUIT GAUCHE ULTRA DENSE ========== */}
      <div className="circuit-left">
        <div className="circuit-line-h-left-1"></div>
        <div className="circuit-line-h-left-2"></div>
        <div className="circuit-line-h-left-3"></div>
        <div className="circuit-line-h-left-4"></div>
        <div className="circuit-line-h-left-5"></div>
        <div className="circuit-line-h-left-6"></div>
        <div className="circuit-line-d-left-1"></div>
        <div className="circuit-line-d-left-2"></div>
        <div className="circuit-line-d-left-3"></div>
        <div className="circuit-line-d-left-4"></div>
        <div className="circuit-line-d-left-5"></div>
        <div className="circuit-line-d-left-6"></div>
        <div className="circuit-line-v-left-1"></div>
        <div className="circuit-line-v-left-2"></div>
        <div className="circuit-line-v-left-3"></div>
        
        {/* NŒUDS GAUCHE */}
        <div className="circuit-node node-left-1"></div>
        <div className="circuit-node node-left-2"></div>
        <div className="circuit-node node-left-3"></div>
        <div className="circuit-node node-left-4"></div>
        <div className="circuit-node node-left-5"></div>
        <div className="circuit-node node-left-6"></div>
        <div className="circuit-node node-left-7"></div>
        <div className="circuit-node node-left-8"></div>
        <div className="circuit-node node-left-9"></div>
        <div className="circuit-node node-left-10"></div>
        <div className="circuit-node node-left-11"></div>
        <div className="circuit-node node-left-12"></div>
        <div className="circuit-node node-left-13"></div>
        
        {/* PETITS NŒUDS GAUCHE */}
        <div className="circuit-node-small node-left-s1"></div>
        <div className="circuit-node-small node-left-s2"></div>
        <div className="circuit-node-small node-left-s3"></div>
        <div className="circuit-node-small node-left-s4"></div>
        <div className="circuit-node-small node-left-s5"></div>
        <div className="circuit-node-small node-left-s6"></div>
        <div className="circuit-node-small node-left-s7"></div>
        <div className="circuit-node-small node-left-s8"></div>
        <div className="circuit-node-small node-left-s9"></div>
        <div className="circuit-node-small node-left-s10"></div>
        <div className="circuit-node-small node-left-s11"></div>
      </div>

      {/* ========== CIRCUIT DROIT ULTRA DENSE ========== */}
      <div className="circuit-right">
        <div className="circuit-line-h-right-1"></div>
        <div className="circuit-line-h-right-2"></div>
        <div className="circuit-line-h-right-3"></div>
        <div className="circuit-line-h-right-4"></div>
        <div className="circuit-line-h-right-5"></div>
        <div className="circuit-line-h-right-6"></div>
        <div className="circuit-line-h-right-7"></div>
        <div className="circuit-line-d-right-1"></div>
        <div className="circuit-line-d-right-2"></div>
        <div className="circuit-line-d-right-3"></div>
        <div className="circuit-line-d-right-4"></div>
        <div className="circuit-line-d-right-5"></div>
        <div className="circuit-line-d-right-6"></div>
        <div className="circuit-line-v-right-1"></div>
        <div className="circuit-line-v-right-2"></div>
        <div className="circuit-line-v-right-3"></div>
        
        {/* NŒUDS DROIT */}
        <div className="circuit-node node-right-1"></div>
        <div className="circuit-node node-right-2"></div>
        <div className="circuit-node node-right-3"></div>
        <div className="circuit-node node-right-4"></div>
        <div className="circuit-node node-right-5"></div>
        <div className="circuit-node node-right-6"></div>
        <div className="circuit-node node-right-7"></div>
        <div className="circuit-node node-right-8"></div>
        <div className="circuit-node node-right-9"></div>
        <div className="circuit-node node-right-10"></div>
        <div className="circuit-node node-right-11"></div>
        <div className="circuit-node node-right-12"></div>
        <div className="circuit-node node-right-13"></div>
        
        {/* PETITS NŒUDS DROIT */}
        <div className="circuit-node-small node-right-s1"></div>
        <div className="circuit-node-small node-right-s2"></div>
        <div className="circuit-node-small node-right-s3"></div>
        <div className="circuit-node-small node-right-s4"></div>
        <div className="circuit-node-small node-right-s5"></div>
        <div className="circuit-node-small node-right-s6"></div>
        <div className="circuit-node-small node-right-s7"></div>
        <div className="circuit-node-small node-right-s8"></div>
        <div className="circuit-node-small node-right-s9"></div>
        <div className="circuit-node-small node-right-s10"></div>
        <div className="circuit-node-small node-right-s11"></div>
      </div>

      {/* ========== VISUALISATION DE DONNÉES GAUCHE ========== */}
      <div className="data-viz-left">
        {leftPixels.map(pixel => (
          <div
            key={pixel.id}
            className={`data-pixel ${pixel.size === 'large' ? 'data-pixel-large' : pixel.size === 'small' ? 'data-pixel-small' : ''}`}
            style={{
              top: pixel.top,
              left: pixel.left,
              animationDelay: pixel.delay,
              animationDuration: pixel.duration
            }}
          ></div>
        ))}
        
        {/* ÉGALISEURS GAUCHE */}
        <div className="equalizer-section equalizer-left-1">
          {leftBars1.map(bar => (
            <div key={bar.id} className="equalizer-bar" style={{ height: bar.height, animationDelay: bar.delay, animationDuration: bar.duration }}></div>
          ))}
        </div>
        <div className="equalizer-section equalizer-left-2">
          {leftBars2.map(bar => (
            <div key={bar.id} className="equalizer-bar" style={{ height: bar.height, animationDelay: bar.delay, animationDuration: bar.duration }}></div>
          ))}
        </div>
        <div className="equalizer-section equalizer-left-3">
          {leftBars3.map(bar => (
            <div key={bar.id} className="equalizer-bar" style={{ height: bar.height, animationDelay: bar.delay, animationDuration: bar.duration }}></div>
          ))}
        </div>

        {/* CODES-BARRES GAUCHE */}
        <div className="barcode-stream barcode-stream-left-1">
          {barcodeLeft1.map(code => (
            <div key={code.id} className="barcode-line" style={{ height: code.height, width: code.width, animationDelay: code.delay }}></div>
          ))}
        </div>
        <div className="barcode-stream barcode-stream-left-2">
          {barcodeLeft2.map(code => (
            <div key={code.id} className="barcode-line" style={{ height: code.height, width: code.width, animationDelay: code.delay }}></div>
          ))}
        </div>
      </div>

      {/* ========== VISUALISATION DE DONNÉES DROITE ========== */}
      <div className="data-viz-right">
        {rightPixels.map(pixel => (
          <div
            key={pixel.id}
            className={`data-pixel ${pixel.size === 'large' ? 'data-pixel-large' : pixel.size === 'small' ? 'data-pixel-small' : ''}`}
            style={{
              top: pixel.top,
              left: pixel.left,
              animationDelay: pixel.delay,
              animationDuration: pixel.duration
            }}
          ></div>
        ))}

        {/* ÉGALISEURS DROITE */}
        <div className="equalizer-section equalizer-right-1">
          {rightBars1.map(bar => (
            <div key={bar.id} className="equalizer-bar" style={{ height: bar.height, animationDelay: bar.delay, animationDuration: bar.duration }}></div>
          ))}
        </div>
        <div className="equalizer-section equalizer-right-2">
          {rightBars2.map(bar => (
            <div key={bar.id} className="equalizer-bar" style={{ height: bar.height, animationDelay: bar.delay, animationDuration: bar.duration }}></div>
          ))}
        </div>
        <div className="equalizer-section equalizer-right-3">
          {rightBars3.map(bar => (
            <div key={bar.id} className="equalizer-bar" style={{ height: bar.height, animationDelay: bar.delay, animationDuration: bar.duration }}></div>
          ))}
        </div>

        {/* CODES-BARRES DROITE */}
        <div className="barcode-stream barcode-stream-right-1">
          {barcodeRight1.map(code => (
            <div key={code.id} className="barcode-line" style={{ height: code.height, width: code.width, animationDelay: code.delay }}></div>
          ))}
        </div>
        <div className="barcode-stream barcode-stream-right-2">
          {barcodeRight2.map(code => (
            <div key={code.id} className="barcode-line" style={{ height: code.height, width: code.width, animationDelay: code.delay }}></div>
          ))}
        </div>
      </div>

      {/* ========== CARTE DE CONNEXION ========== */}
      <form className="login-form" onSubmit={handleSubmit}>
        <div className="login-header">
          <FaUser className="login-icon" />
          <h2>LOGIN</h2>
          <div className="header-line"></div>
        </div>

        <div className="input-group">
          <FaEnvelope className="input-icon" />
          <span className="bracket-top-left">[</span>
          <span className="bracket-top-right">]</span>
          <input
            type="email"
            placeholder="Email ID"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div className="input-group">
          <FaLock className="input-icon" />
          <span className="bracket-top-left">[</span>
          <span className="bracket-top-right">]</span>
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <div className="options">
          <label>
            <input
              type="checkbox"
              checked={remember}
              onChange={() => setRemember(!remember)}
            />
            Remember me
          </label>
          <a href="#" className="forgot-link">Forgot Password?</a>
        </div>

        <button type="submit" className="login-btn">LOGIN</button>
      </form>
      
      {/* Effet de lueur suivant la souris */}
      <div 
        style={{
          position: 'absolute',
          width: '400px',
          height: '400px',
          background: 'radial-gradient(circle, rgba(0,150,255,0.1) 0%, transparent 70%)',
          left: mousePosition.x - 200,
          top: mousePosition.y - 200,
          pointerEvents: 'none',
          zIndex: 30,
          mixBlendMode: 'screen',
          transition: 'left 0.1s, top 0.1s'
        }}
      ></div>
    </div>
  );
}

export default Login;
