import React, { useEffect, useState } from "react";
import { Navigate } from "react-router-dom";
import axios from "axios";
import "../styles/StockManagerDashboard.css";

function StockManagerDashboard({ user }) {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [scanResult, setScanResult] = useState(null);

  // Charger tous les produits
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("http://localhost:5000/api/products", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProducts(res.data.data || []);
      } catch (err) {
        console.error("Erreur chargement produits", err);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  // VÃ©rification du rÃ´le
  if (!user || user.role !== "responsable_stock") {
    return <Navigate to="/unauthorized" />;
  }

  if (loading) return <p className="loading-text">Chargement...</p>;

  return (
    <div className="dashboard-root">
      <div className="dashboard-container">
        <h1>ðŸ“¦ Gestion des Stocks ðŸ“¦</h1>

        {scanResult && (
          <div className="scan-result">
            RÃ©sultat du scan : <strong>{scanResult}</strong>
          </div>
        )}

        <h2>Liste des produits</h2>
        <table className="product-table">
          <thead>
            <tr>
              <th>Nom</th>
              <th>Description</th>
              <th>QuantitÃ©</th>
              <th>Prix</th>
            </tr>
          </thead>
          <tbody>
            {products.map((p) => (
              <tr key={p._id}>
                <td>{p.name}</td>
                <td>{p.description}</td>
                <td>
                  <span
                    style={{
                      background:
                        p.quantity > 0
                          ? "rgba(76, 175, 80, 0.2)"
                          : "rgba(244, 67, 54, 0.2)",
                      color: p.quantity > 0 ? "#4caf50" : "#f44336",
                      padding: "0.3rem 0.8rem",
                      borderRadius: "20px",
                      fontWeight: "600",
                    }}
                  >
                    {p.quantity}
                  </span>
                </td>
                <td>{p.price} â‚¬</td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="dashboard-actions">
          <button
            onClick={() => {
              localStorage.clear();
              window.location.href = "/";
            }}
          >
            ðŸšª DÃ©connexion
          </button>
        </div>
      </div>
    </div>
  );
}

export default StockManagerDashboard;
