import React, { useEffect, useState } from 'react';
import { Navigate } from 'react-router-dom';
import "../styles/EngineerDashboard.css";
import { QRCodeSVG } from "qrcode.react";
import BarcodeScannerComponent from "react-qr-barcode-scanner";

function StockManagerDashboard({ user }) {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [scannedProduct, setScannedProduct] = useState(null);

  // Charger tous les produits
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/products', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        const data = await res.json();
        setProducts(data.data || []);
      } catch (err) {
        console.error('Erreur lors du chargement des produits', err);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  // Récupérer un produit scanné par son ID
  const fetchProductById = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/products/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });
      const data = await res.json();
      if (data.success) {
        setScannedProduct(data.data);
      }
    } catch (err) {
      console.error("Erreur récupération produit scanné", err);
    }
  };

  // Vérification du rôle
  if (!user || user.role !== 'responsable_stock') {
    return <Navigate to="/unauthorized" />;
  }

  if (loading) return <p className="loading-text">Chargement...</p>;

  // Fonction pour mettre à jour la quantité
  const updateQuantity = async (productId, action) => {
    const product = products.find(p => p._id === productId);
    if (!product) return;

    const newQuantity = action === "add" ? product.quantity + 1 : product.quantity - 1;

    try {
      const res = await fetch(`http://localhost:5000/api/products/${productId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`
        },
        body: JSON.stringify({ quantity: newQuantity })
      });
      const data = await res.json();
      if (data.success) {
        // Mettre à jour la liste des produits
        setProducts(products.map(p => p._id === productId ? { ...p, quantity: newQuantity } : p));
        // Mettre à jour le produit scanné
        setScannedProduct({ ...scannedProduct, quantity: newQuantity });
      }
    } catch (err) {
      console.error("Erreur mise à jour quantité", err);
    }
  };

  return (
    <div className="dashboard-root">
      <h1>Dashboard Responsable Stock</h1>
      <h2>Liste des produits et quantités disponibles</h2>

      <table className="product-table">
        <thead>
          <tr>
            <th>Nom</th>
            <th>Description</th>
            <th>Prix</th>
            <th>Quantité disponible</th>
            <th>QR Code</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p._id}>
              <td>{p.name}</td>
              <td>{p.description}</td>
              <td>{p.price} €</td>
              <td>{p.quantity}</td>
              <td><QRCodeSVG value={p._id} size={64} /></td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Scanner un produit</h2>
      <BarcodeScannerComponent
        width={300}
        height={300}
        onUpdate={(err, result) => {
          if (result) {
            fetchProductById(result.text); // récupérer infos produit
          }
        }}
      />

      {scannedProduct && (
        <div className="dashboard-actions">
          <h3>{scannedProduct.name}</h3>
          <p>Quantité actuelle : {scannedProduct.quantity}</p>
          <button onClick={() => updateQuantity(scannedProduct._id, "add")}>➕ Ajouter</button>
          <button onClick={() => updateQuantity(scannedProduct._id, "remove")}>➖ Supprimer</button>
        </div>
      )}

      <div className="dashboard-actions">
        <button onClick={() => {
          localStorage.clear();
          window.location.href = "/";
        }}>
          Déconnexion
        </button>
      </div>
    </div>
  );
}

export default StockManagerDashboard;
