import React, { useState } from "react";
import "../styles/Login.css";
import { FaUser, FaLock } from "react-icons/fa";

function LoginCyber() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [remember, setRemember] = useState(false);

  return (
    <div className="cyber-container">
      {/* Simple halo lumineux */}
      <div className="cyber-glow"></div>
      
      {/* Panneau principal */}
      <div className="cyber-panel">
        <div className="cyber-header">
          <div className="cyber-line"></div>
          <h1>ACCÈS</h1>
          <h2>TERMINAL</h2>
          <div className="cyber-line"></div>
        </div>

        <form onSubmit={(e) => e.preventDefault()}>
          <div className="cyber-field">
            <FaUser className="cyber-icon" />
            <input 
              type="email" 
              placeholder="IDENTIFIANT"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <div className="field-focus"></div>
          </div>

          <div className="cyber-field">
            <FaLock className="cyber-icon" />
            <input 
              type="password" 
              placeholder="CODE"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <div className="field-focus"></div>
          </div>

          <div className="cyber-options">
            <label className="cyber-check">
              <input 
                type="checkbox" 
                checked={remember}
                onChange={() => setRemember(!remember)}
              />
              <span>Mémoriser</span>
            </label>
            <a href="#" className="cyber-link">Réinitialiser</a>
          </div>

          <button type="submit" className="cyber-button">
            <span>VALIDER</span>
            <div className="button-glow"></div>
          </button>
        </form>

        <div className="cyber-footer">
          <span>© 2026 • NEURONET</span>
        </div>
      </div>

      {/* Quelques points lumineux discrets */}
      <div className="cyber-dot dot-1"></div>
      <div className="cyber-dot dot-2"></div>
      <div className="cyber-dot dot-3"></div>
    </div>
  );
}

export default LoginCyber;
