import React, { useEffect, useState } from 'react';
import { Navigate } from 'react-router-dom';
import "../styles/EngineerDashboard.css";
import { QRCodeSVG } from "qrcode.react";
import BarcodeScannerComponent from "react-qr-barcode-scanner"; // ✅ alternative compatible React 19

function StockManagerDashboard({ user }) {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [scannedProductId, setScannedProductId] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/products', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        const data = await res.json();
        setProducts(data.data || []);
      } catch (err) {
        console.error('Erreur lors du chargement des produits', err);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  if (!user || user.role !== 'responsable_stock') {
    return <Navigate to="/unauthorized" />;
  }

  if (loading) return <p className="loading-text">Chargement...</p>;

  // Fonction pour mettre à jour la quantité
  const updateQuantity = async (productId, action) => {
    const product = products.find(p => p._id === productId);
    if (!product) return;

    const newQuantity = action === "add" ? product.quantity + 1 : product.quantity - 1;

    try {
      const res = await fetch(`http://localhost:5000/api/products/${productId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`
        },
        body: JSON.stringify({ quantity: newQuantity })
      });
      const data = await res.json();
      if (data.success) {
        setProducts(products.map(p => p._id === productId ? { ...p, quantity: newQuantity } : p));
        alert(`Quantité mise à jour: ${newQuantity}`);
      }
    } catch (err) {
      console.error("Erreur mise à jour quantité", err);
    }
  };

  return (
    <div className="dashboard-root">
      <h1>Dashboard Responsable Stock</h1>
      <h2>Liste des produits et quantités disponibles</h2>

      <table className="product-table">
        <thead>
          <tr>
            <th>Nom</th>
            <th>Description</th>
            <th>Prix</th>
            <th>Quantité disponible</th>
            <th>QR Code</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p._id}>
              <td>{p.name}</td>
              <td>{p.description}</td>
              <td>{p.price} €</td>
              <td>{p.quantity}</td>
              <td><QRCodeSVG value={p._id} size={64} /></td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Scanner un produit</h2>
      <BarcodeScannerComponent
        width={300}
        height={300}
        onUpdate={(err, result) => {
          if (result) {
            setScannedProductId(result.text);
          }
        }}
      />

      {scannedProductId && (
        <div className="dashboard-actions">
          <p>Produit scanné : {scannedProductId}</p>
          <button onClick={() => updateQuantity(scannedProductId, "add")}>➕ Ajouter</button>
          <button onClick={() => updateQuantity(scannedProductId, "remove")}>➖ Supprimer</button>
        </div>
      )}

      <div className="dashboard-actions">
        <button onClick={() => {
          localStorage.clear();
          window.location.href = "/";
        }}>
          Déconnexion
        </button>
      </div>
    </div>
  );
}

export default StockManagerDashboard;
