import React, { useEffect, useState } from "react";

function UpdateUser({ userId }) {
  const [userData, setUserData] = useState({
    name: "",
    email: "",
    password: ""
  });
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const token = localStorage.getItem("token"); // token JWT pour authentification

  // 1️⃣ Récupérer les données de l'utilisateur à modifier
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await fetch(`http://localhost:5000/api/users/${userId}`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        const data = await res.json();
        if (data.success) {
          setUserData({
            name: data.user.name,
            email: data.user.email,
            password: "" // ne pas remplir le mot de passe
          });
        } else {
          setMessage(data.message || "Impossible de récupérer les données");
        }
      } catch (err) {
        console.error(err);
        setMessage("Erreur lors de la récupération des données");
      }
    };

    fetchUser();
  }, [userId, token]);

  // 2️⃣ Mettre à jour les champs
  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  // 3️⃣ Soumettre la modification
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const res = await fetch(`http://localhost:5000/api/users/${userId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify(userData)
      });

      const data = await res.json();

      if (data.success) {
        setMessage("Utilisateur mis à jour avec succès !");
      } else {
        setMessage(data.message || "Erreur lors de la mise à jour");
      }
    } catch (err) {
      console.error(err);
      setMessage("Erreur serveur lors de la mise à jour");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: "500px", margin: "20px auto", padding: "20px", border: "1px solid #ddd", borderRadius: "10px" }}>
      <h2>Modifier l'utilisateur</h2>

      {message && <p>{message}</p>}

      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: "10px" }}>
          <label>Nom :</label>
          <input
            type="text"
            name="name"
            value={userData.name}
            onChange={handleChange}
            required
            disabled={loading}
            style={{ width: "100%", padding: "8px" }}
          />
        </div>

        <div style={{ marginBottom: "10px" }}>
          <label>Email :</label>
          <input
            type="email"
            name="email"
            value={userData.email}
            onChange={handleChange}
            required
            disabled={loading}
            style={{ width: "100%", padding: "8px" }}
          />
        </div>

        <div style={{ marginBottom: "10px" }}>
          <label>Mot de passe :</label>
          <input
            type="password"
            name="password"
            value={userData.password}
            onChange={handleChange}
            placeholder="Laissez vide si pas de modification"
            disabled={loading}
            style={{ width: "100%", padding: "8px" }}
          />
        </div>

        <button type="submit" disabled={loading} style={{ padding: "10px 20px" }}>
          {loading ? "Mise à jour..." : "Mettre à jour"}
        </button>
      </form>
    </div>
  );
}

export default UpdateUser;
