import React, { useEffect, useState } from "react";
import { useParams, Navigate } from "react-router-dom";
import axios from "axios";
import "../styles/UpdateUser.css";

function UpdateUser({ user }) {
  const { id } = useParams(); // ID de l'utilisateur à modifier
  const [userData, setUserData] = useState({
    name: "",
    email: "",
    role: ""
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Vérification d'accès
  if (
    !user ||
    (user.role !== "directeur" &&
      user.role !== "admin" &&
      user._id !== id)
  ) {
    return <Navigate to="/unauthorized" />;
  }

  // Récupérer les infos de l'utilisateur
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get(`http://localhost:5000/api/users/${id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUserData({
          name: res.data.data.name,
          email: res.data.data.email,
          role: res.data.data.role,
        });
      } catch (err) {
        console.error("Erreur chargement utilisateur:", err);
        setError("Impossible de charger les données de l'utilisateur");
      } finally {
        setLoading(false);
      }
    };
    fetchUser();
  }, [id]);

  // Soumettre les modifications
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      const res = await axios.put(
        `http://localhost:5000/api/users/${id}`,
        userData,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Utilisateur mis à jour !");
    } catch (err) {
      console.error("Erreur mise à jour:", err);
      alert("Impossible de mettre à jour l'utilisateur.");
    }
  };

  if (loading) return <p>Chargement...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="update-user-container">
      <h1>Modifier le profil</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Nom :
          <input
            type="text"
            value={userData.name}
            onChange={(e) =>
              setUserData({ ...userData, name: e.target.value })
            }
            required
          />
        </label>

        <label>
          Email :
          <input
            type="email"
            value={userData.email}
            onChange={(e) =>
              setUserData({ ...userData, email: e.target.value })
            }
            required
          />
        </label>

        {/* Seuls les admins/directors peuvent modifier le rôle */}
        {(user.role === "directeur" || user.role === "admin") && (
          <label>
            Rôle :
            <select
              value={userData.role}
              onChange={(e) =>
                setUserData({ ...userData, role: e.target.value })
              }
              required
            >
              <option value="ingénieur">Ingénieur</option>
              <option value="responsable_stock">Responsable Stock</option>
              <option value="directeur">Directeur</option>
              <option value="admin">Admin</option>
            </select>
          </label>
        )}

        <button type="submit">Mettre à jour</button>
      </form>

      <button
        className="logout-btn"
        onClick={() => {
          localStorage.clear();
          window.location.href = "/";
        }}
      >
        Déconnexion
      </button>
    </div>
  );
}

export default UpdateUser;
