import React, { useEffect, useState, useCallback } from 'react';
import { Navigate } from 'react-router-dom';
import "../styles/EngineerDashboard.css";
import { QRCodeSVG } from "qrcode.react";
import BarcodeScannerComponent from "react-qr-barcode-scanner";

function StockManagerDashboard({ user }) {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [scannedProduct, setScannedProduct] = useState(null);
  const [isScanning, setIsScanning] = useState(true);
  const [scanError, setScanError] = useState("");

  // Charger tous les produits
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/products', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        const data = await res.json();
        setProducts(data.data || []);
      } catch (err) {
        console.error('Erreur lors du chargement des produits', err);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  // V√©rification du r√¥le
  if (!user || user.role !== 'responsable_stock') {
    return <Navigate to="/unauthorized" />;
  }

  if (loading) return <p className="loading-text">Chargement...</p>;

  // Fonction pour mettre √† jour la quantit√©
  const updateQuantity = async (productId, action) => {
    const product = products.find(p => p._id === productId);
    if (!product) return;

    const newQuantity = action === "add" ? product.quantity + 1 : product.quantity - 1;
    
    if (newQuantity < 0) {
      alert("La quantit√© ne peut pas √™tre n√©gative");
      return;
    }

    try {
      const res = await fetch(`http://localhost:5000/api/products/${productId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`
        },
        body: JSON.stringify({ quantity: newQuantity })
      });
      const data = await res.json();
      if (data.success) {
        setProducts(products.map(p => p._id === productId ? { ...p, quantity: newQuantity } : p));
        setScannedProduct(prev => ({ ...prev, quantity: newQuantity }));
      }
    } catch (err) {
      console.error("Erreur mise √† jour quantit√©", err);
    }
  };

  // CHANGEMENT ICI : Recherche du produit par TOUS les champs possibles
  const findProductByScannedData = async (scannedText) => {
    setScanError("");
    
    // 1. Essayer de trouver dans les produits d√©j√† charg√©s
    let product = products.find(p => 
      p._id === scannedText || 
      p.name === scannedText || 
      p.barcode === scannedText ||
      p.reference === scannedText
    );

    if (product) {
      setScannedProduct(product);
      return;
    }

    // 2. Si pas trouv√©, chercher par ID dans l'API
    try {
      const res = await fetch(`http://localhost:5000/api/products/${scannedText}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });
      const data = await res.json();
      if (data.success) {
        setScannedProduct(data.data);
        return;
      }
    } catch (err) {
      console.log("Pas un ID valide, recherche par nom...");
    }

    // 3. Chercher par nom dans l'API (recherche approximative)
    try {
      const res = await fetch(`http://localhost:5000/api/products?search=${encodeURIComponent(scannedText)}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });
      const data = await res.json();
      if (data.data && data.data.length > 0) {
        setScannedProduct(data.data[0]); // Prendre le premier r√©sultat
        return;
      }
    } catch (err) {
      console.error("Erreur recherche produit", err);
    }

    // 4. Produit non trouv√©
    setScanError(`Produit non trouv√©: "${scannedText}"`);
    console.log("Produit non trouv√©:", scannedText);
  };

  const handleScan = (err, result) => {
    if (err) {
      console.error("Erreur de scan:", err);
      setScanError("Erreur de scan, r√©essayez");
      return;
    }
    
    if (result) {
      const scannedText = result.text.trim();
      console.log("QR Code scann√©:", scannedText);
      findProductByScannedData(scannedText);
    }
  };

  return (
    <div className="dashboard-root">
      <h1>üì¶ Dashboard Responsable Stock</h1>
      
      <div className="stats-summary">
        <div className="stat-card">
          <h3>üìä Produits en stock</h3>
          <p className="stat-number">{products.length}</p>
        </div>
        <div className="stat-card">
          <h3>üì¶ Total articles</h3>
          <p className="stat-number">
            {products.reduce((sum, p) => sum + p.quantity, 0)}
          </p>
        </div>
      </div>

      <div className="scanner-section">
        <h2>üì∑ Scanner un QR code</h2>
        <div className="scanner-wrapper">
          {isScanning && (
            <BarcodeScannerComponent
              width={400}
              height={300}
              onUpdate={handleScan}
            />
          )}
          <div className="scanner-controls">
            <button 
              className={`scanner-btn ${isScanning ? 'active' : ''}`}
              onClick={() => setIsScanning(!isScanning)}
            >
              {isScanning ? "‚è∏Ô∏è Pause scanner" : "‚ñ∂Ô∏è Activer scanner"}
            </button>
          </div>
        </div>
        
        {scanError && (
          <div className="error-message">
            ‚ö†Ô∏è {scanError}
          </div>
        )}
      </div>

      {scannedProduct && (
        <div className="scanned-product-container">
          <h2>üìã Produit scann√©</h2>
          <div className="product-detail-card">
            <div className="product-header">
              <h3>{scannedProduct.name}</h3>
              <span className="product-id">ID: {scannedProduct._id.substring(0, 8)}...</span>
            </div>
            
            <div className="product-info">
              <div className="info-row">
                <span className="label">Description:</span>
                <span>{scannedProduct.description || "‚Äî"}</span>
              </div>
              <div className="info-row">
                <span className="label">Prix:</span>
                <span className="price">{scannedProduct.price} ‚Ç¨</span>
              </div>
              <div className="info-row quantity-row">
                <span className="label">Quantit√©:</span>
                <span className="quantity">{scannedProduct.quantity}</span>
              </div>
            </div>

            <div className="product-actions">
              <button 
                className="btn-add"
                onClick={() => updateQuantity(scannedProduct._id, "add")}
              >
                ‚ûï Ajouter un produit
              </button>
              <button 
                className="btn-remove"
                onClick={() => updateQuantity(scannedProduct._id, "remove")}
                disabled={scannedProduct.quantity <= 0}
              >
                ‚ûñ Retirer un produit
              </button>
            </div>
          </div>
        </div>
      )}

      <h2 className="products-title">üìã Tous les produits</h2>
      <div className="table-container">
        <table className="product-table">
          <thead>
            <tr>
              <th>Nom</th>
              <th>Description</th>
              <th>Prix</th>
              <th>Quantit√©</th>
              <th>QR Code</th>
            </tr>
          </thead>
          <tbody>
            {products.map(p => (
              <tr key={p._id}>
                <td>{p.name}</td>
                <td>{p.description || "‚Äî"}</td>
                <td>{p.price} ‚Ç¨</td>
                <td>
                  <span className={`quantity-badge ${p.quantity <= 5 ? 'low-stock' : ''}`}>
                    {p.quantity}
                  </span>
                </td>
                <td>
                  <QRCodeSVG 
                    value={p._id} 
                    size={50}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="dashboard-footer">
        <button 
          className="logout-btn"
          onClick={() => {
            localStorage.clear();
            window.location.href = "/";
          }}
        >
          üö™ D√©connexion
        </button>
      </div>
    </div>
  );
}

export default StockManagerDashboard;
