import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './pages/Login';
import EngineerDashboard from './pages/EngineerDashboard';
import StockManagerDashboard from './pages/StockManagerDashboard';

function App() {
  const currentUser = {
    role: 'ingénieur' // exemple, à remplacer par ton vrai user après login
  };

  return (
    <Router>
      <Routes>
        {/* Page de connexion */}
        <Route path="/" element={<Login />} />

        {/* Dashboard Ingénieur */}
        <Route
          path="/engineer-dashboard"
          element={<EngineerDashboard user={currentUser} />}
        />

        {/* Dashboard Responsable Stock */}
        <Route
          path="/stock-dashboard"
          element={<StockManagerDashboard user={currentUser} />}
        />
      </Routes>
    </Router>
  );
}

export default App;
