import React, { useState } from "react";
import "../styles/Login.css";
import { FaUser, FaEnvelope, FaLock } from "react-icons/fa";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [remember, setRemember] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });
      const data = await res.json();
      if (data.success) {
        alert("✅ Connexion réussie !");
        localStorage.setItem("token", data.token);
      } else {
        alert("❌ Identifiants invalides");
      }
    } catch (error) {
      console.error("Erreur:", error);
    }
  };

  // Générer des positions aléatoires pour les éléments de visualisation
  const generateDataPixels = (count, side) => {
    return Array.from({ length: count }).map((_, i) => ({
      id: i,
      top: `${Math.random() * 80 + 10}%`,
      left: side === 'left' ? `${Math.random() * 60}%` : `${Math.random() * 60 + 40}%`,
      delay: `${Math.random() * 5}s`,
      duration: `${Math.random() * 3 + 2}s`
    }));
  };

  const leftPixels = generateDataPixels(40, 'left');
  const rightPixels = generateDataPixels(40, 'right');

  // Générer des barres d'égaliseur
  const generateEqualizerBars = (count, position) => {
    return Array.from({ length: count }).map((_, i) => ({
      id: i,
      left: `${i * 8}px`,
      height: `${Math.floor(Math.random() * 30 + 15)}px`,
      delay: `${Math.random() * 2}s`
    }));
  };

  const leftBars = generateEqualizerBars(15, 'left');
  const rightBars = generateEqualizerBars(15, 'right');

  return (
    <div className="login-container">
      {/* Éléments de circuit - Gauche */}
      <div className="circuit-left">
        <div className="circuit-line" style={{ width: '80px', height: '2px', top: '30%', left: '20%', transform: 'rotate(45deg)' }}></div>
        <div className="circuit-line" style={{ width: '120px', height: '2px', top: '50%', left: '10%' }}></div>
        <div className="circuit-line" style={{ width: '60px', height: '2px', top: '70%', left: '30%', transform: 'rotate(-45deg)' }}></div>
        <div className="circuit-node" style={{ top: '30%', left: 'calc(20% + 55px)' }}></div>
        <div className="circuit-node" style={{ top: '50%', left: 'calc(10% + 115px)' }}></div>
        <div className="circuit-node" style={{ top: '70%', left: 'calc(30% + 40px)' }}></div>
      </div>

      {/* Éléments de circuit - Droite */}
      <div className="circuit-right">
        <div className="circuit-line" style={{ width: '80px', height: '2px', top: '30%', right: '20%', transform: 'rotate(-45deg)' }}></div>
        <div className="circuit-line" style={{ width: '120px', height: '2px', top: '50%', right: '10%' }}></div>
        <div className="circuit-line" style={{ width: '60px', height: '2px', top: '70%', right: '30%', transform: 'rotate(45deg)' }}></div>
        <div className="circuit-node" style={{ top: '30%', right: 'calc(20% + 55px)' }}></div>
        <div className="circuit-node" style={{ top: '50%', right: 'calc(10% + 115px)' }}></div>
        <div className="circuit-node" style={{ top: '70%', right: 'calc(30% + 40px)' }}></div>
      </div>

      {/* Visualisation de données - Nuages de points gauche */}
      <div className="data-viz-left">
        {leftPixels.map(pixel => (
          <div
            key={pixel.id}
            className="data-pixel"
            style={{
              top: pixel.top,
              left: pixel.left,
              animationDelay: pixel.delay,
              animationDuration: pixel.duration
            }}
          ></div>
        ))}
        
        {/* Barres d'égaliseur gauche */}
        <div className="equalizer-bars" style={{ bottom: '20%', left: '20%' }}>
          {leftBars.map(bar => (
            <div
              key={bar.id}
              className="equalizer-bar"
              style={{
                height: bar.height,
                animationDelay: bar.delay
              }}
            ></div>
          ))}
        </div>
      </div>

      {/* Visualisation de données - Nuages de points droite */}
      <div className="data-viz-right">
        {rightPixels.map(pixel => (
          <div
            key={pixel.id}
            className="data-pixel"
            style={{
              top: pixel.top,
              left: pixel.left,
              animationDelay: pixel.delay,
              animationDuration: pixel.duration
            }}
          ></div>
        ))}
        
        {/* Barres d'égaliseur droite */}
        <div className="equalizer-bars" style={{ bottom: '30%', right: '20%' }}>
          {rightBars.map(bar => (
            <div
              key={bar.id}
              className="equalizer-bar"
              style={{
                height: bar.height,
                animationDelay: bar.delay
              }}
            ></div>
          ))}
        </div>
      </div>

      {/* Carte de connexion */}
      <form className="login-form" onSubmit={handleSubmit}>
        <div className="login-header">
          <FaUser className="login-icon" />
          <h2>LOGIN</h2>
        </div>

        <div className="input-group">
          <FaEnvelope className="input-icon" />
          <input
            type="email"
            placeholder="Email ID"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div className="input-group">
          <FaLock className="input-icon" />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <div className="options">
          <label>
            <input
              type="checkbox"
              checked={remember}
              onChange={() => setRemember(!remember)}
            />
            Remember me
          </label>
          <a href="#" className="forgot-link">Forgot Password?</a>
        </div>

        <button type="submit" className="login-btn">LOGIN</button>
      </form>
    </div>
  );
}

export default Login;
