import React, { useState, useEffect } from "react";
import "./Login.css";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [remember, setRemember] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      if (data.success) {
        localStorage.setItem("token", data.token);
        localStorage.setItem("role", data.user.role);

        if (data.user.role === "ingénieur") {
          window.location.href = "/engineer-dashboard";
        } else if (data.user.role === "responsable_stock") {
          window.location.href = "/stock-dashboard";
        } else {
          alert("Accès refusé : rôle non autorisé");
        }
      } else {
        alert(data.message || "Échec de la connexion");
      }
    } catch (err) {
      console.error("Erreur de connexion:", err);
      alert("Erreur serveur");
    }
  };

  useEffect(() => {
    // Charger un thème par défaut (ici le premier thème)
    const themes = [
      {
        background: "#000000",
        color: "#ffffff",
        primary: "#009688",
      },
      {
        background: "#ffffff",
        color: "#000000",
        primary: "#009688",
      },
      {
        background: "#1a1a2e",
        color: "#ffffff",
        primary: "#0f3460",
      },
      {
        background: "#f8f9fa",
        color: "#343a40",
        primary: "#007bff",
      },
    ];

    const setTheme = (theme) => {
      document.documentElement.style.setProperty("--background", theme.background);
      document.documentElement.style.setProperty("--color", theme.color);
      document.documentElement.style.setProperty("--primary-color", theme.primary);
    };

    setTheme(themes[0]);
  }, []);

  return (
    <div className="container">
      <div className="login-container">
        <div className="circle circle-one"></div>
        <div className="circle circle-two"></div>

        <div className="form-container">
          <h1 className="mot-connexion">Connexion</h1>
          <form onSubmit={handleSubmit}>
            <input
              type="email"
              placeholder="Adresse email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />

            <input
              type="password"
              placeholder="Mot de passe"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />

            <div className="register-forget opacity">
              <label>
                <input
                  type="checkbox"
                  checked={remember}
                  onChange={(e) => setRemember(e.target.checked)}
                />
                Se souvenir de moi
              </label>
              <a href="#">Mot de passe oublié ?</a>
            </div>

            <button type="submit">Se connecter</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
