import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "../styles/Login.css";

function UpdateUser() {
  const { id } = useParams(); // <- prend l'id depuis l'URL
  const navigate = useNavigate();

  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUser = async () => {
      if (!id) return; // sécurité
      try {
        const res = await fetch(`http://localhost:5000/api/users/${id}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });
        if (!res.ok) {
          throw new Error("Erreur de récupération de l'utilisateur");
        }
        const data = await res.json();
        setName(data.name || "");
        setEmail(data.email || "");
      } catch (err) {
        console.error(err);
        alert("Impossible de récupérer l'utilisateur");
      } finally {
        setLoading(false);
      }
    };

    fetchUser();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`http://localhost:5000/api/users/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({ name, email, password }),
      });
      const data = await res.json();

      if (!data.success) {
        alert(data.message || "Erreur lors de la mise à jour");
      } else {
        alert("Utilisateur mis à jour avec succès !");
        navigate("/engineer-dashboard"); // ou dashboard selon rôle
      }
    } catch (err) {
      console.error(err);
      alert("Erreur lors de la mise à jour de l'utilisateur");
    }
  };

  if (loading) return <p className="loading-text">Chargement...</p>;

  return (
    <div className="login-container">
      <h1>Modifier Utilisateur</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nom"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Nouveau mot de passe (laisser vide si inchangé)"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Mettre à jour</button>
      </form>
    </div>
  );
}

export default UpdateUser;
