import React, { useState } from "react";
import "../styles/Login.css";
import { FaUser, FaEnvelope, FaLock } from "react-icons/fa";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [remember, setRemember] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });
      const data = await res.json();
      if (data.success) {
        alert("✅ Connexion réussie !");
        localStorage.setItem("token", data.token);
      } else {
        alert("❌ Identifiants invalides");
      }
    } catch (error) {
      console.error("Erreur:", error);
    }
  };

  return (
    <div className="login-container">
      {/* Halo central - Radial Gradient (déjà dans CSS mais on renforce) */}
      <div className="halo-core"></div>
      
      {/* Nuages de données - Data Maps (structure HTML pour plus de contrôle) */}
      <div className="data-cloud left-cloud">
        <div className="pixel-cluster"></div>
        <div className="pixel-cluster-2"></div>
        <div className="pixel-cluster-3"></div>
      </div>
      <div className="data-cloud right-cloud">
        <div className="pixel-cluster"></div>
        <div className="pixel-cluster-2"></div>
        <div className="pixel-cluster-3"></div>
      </div>

      {/* Panneau de connexion principal */}
      <form className="login-form" onSubmit={handleSubmit}>
        {/* Crochets HUD - Décoration supérieure */}
        <div className="hud-corner top-left"></div>
        <div className="hud-corner top-right"></div>
        
        {/* Header avec icône et titre */}
        <div className="login-header">
          <div className="header-bracket left-bracket"></div>
          <FaUser className="login-icon" />
          <h2>LOGIN</h2>
          <div className="header-bracket right-bracket"></div>
        </div>

        {/* Barres de chargement latérales intégrées au formulaire */}
        <div className="equalizer left-equalizer">
          <span></span><span></span><span></span><span></span>
          <span></span><span></span><span></span><span></span>
        </div>
        <div className="equalizer right-equalizer">
          <span></span><span></span><span></span><span></span>
          <span></span><span></span><span></span><span></span>
        </div>

        {/* Champ Email */}
        <div className="input-group">
          <FaEnvelope className="input-icon" />
          <input
            type="email"
            placeholder="EMAIL ID"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <div className="input-underline"></div>
        </div>

        {/* Champ Password */}
        <div className="input-group">
          <FaLock className="input-icon" />
          <input
            type="password"
            placeholder="PASSWORD"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <div className="input-underline"></div>
        </div>

        {/* Options Remember me / Forgot password */}
        <div className="options">
          <label className="remember-label">
            <input
              type="checkbox"
              checked={remember}
              onChange={() => setRemember(!remember)}
            />
            <span className="checkmark"></span>
            Remember me
          </label>
          <a href="#" className="forgot-link">Forgot Password?</a>
        </div>

        {/* Bouton Login */}
        <button type="submit" className="login-btn">
          <span>LOGIN</span>
          <div className="btn-glow"></div>
        </button>

        {/* Crochets HUD - Décoration inférieure */}
        <div className="hud-corner bottom-left"></div>
        <div className="hud-corner bottom-right"></div>
      </form>

      {/* LIGNES DE CIRCUIT - CONNEXIONS LATÉRALES */}
      {/* Ligne supérieure gauche */}
      <div className="circuit-line circuit-line-1">
        <div className="circuit-path"></div>
        <div className="circuit-node"></div>
        <div className="circuit-node-glow"></div>
      </div>
      
      {/* Ligne inférieure gauche */}
      <div className="circuit-line circuit-line-2">
        <div className="circuit-path"></div>
        <div className="circuit-node"></div>
        <div className="circuit-node-glow"></div>
      </div>
      
      {/* Ligne supérieure droite */}
      <div className="circuit-line circuit-line-3">
        <div className="circuit-path"></div>
        <div className="circuit-node"></div>
        <div className="circuit-node-glow"></div>
      </div>
      
      {/* Ligne inférieure droite */}
      <div className="circuit-line circuit-line-4">
        <div className="circuit-path"></div>
        <div className="circuit-node"></div>
        <div className="circuit-node-glow"></div>
      </div>

      {/* Lignes de circuit secondaires (effet de réseau) */}
      <div className="circuit-line-secondary line-sec-1"></div>
      <div className="circuit-line-secondary line-sec-2"></div>
      <div className="circuit-line-secondary line-sec-3"></div>
      <div className="circuit-line-secondary line-sec-4"></div>

      {/* Points lumineux flottants supplémentaires */}
      <div className="floating-node node-1"></div>
      <div className="floating-node node-2"></div>
      <div className="floating-node node-3"></div>
      <div className="floating-node node-4"></div>
      <div className="floating-node node-5"></div>
      <div className="floating-node node-6"></div>
    </div>
  );
}

export default Login;
