// src/pages/Products.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Products = () => {
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // ✅ Remplace par l'URL exacte de ton backend (port)
        const API_URL = "http://localhost:8080/api"; // ajuste si ton port est différent
        const res = await fetch(`${API_URL}/products`);

        if (!res.ok) {
          throw new Error(`Erreur HTTP: ${res.status} ${res.statusText}`);
        }

        const data = await res.json();

        // Ton backend renvoie { success, count, total, totalPages, currentPage, data: [...] }
        if (data.success && Array.isArray(data.data)) {
          setProducts(data.data);
        } else {
          throw new Error("Format de réponse invalide");
        }
      } catch (err) {
        console.error("Erreur lors du chargement des produits", err);
        alert("Impossible de charger les produits. Vérifie que le backend est lancé.");
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading) {
    return <div>Chargement des produits...</div>;
  }

  return (
    <div className="products-page">
      <h1>Gestion des produits</h1>
      <p>Liste de tous les produits (fournisseur et internes)</p>

      <button onClick={() => navigate("/add-product")}>
        Ajouter un produit
      </button>

      <table className="products-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nom</th>
            <th>Description</th>
            <th>Prix</th>
            <th>Quantité</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.length === 0 ? (
            <tr>
              <td colSpan="6">Aucun produit trouvé.</td>
            </tr>
          ) : (
            products.map((p) => (
              <tr key={p.id}>
                <td>{p.id}</td>
                <td>{p.name}</td>
                <td>{p.description}</td>
                <td>{p.price} DT</td>
                <td>{p.quantity}</td>
                <td>
                  <button
                    onClick={() => navigate(`/update-product/${p.id}`)}
                    style={{ marginRight: "8px" }}
                  >
                    Modifier
                  </button>
                  <button
                    onClick={() => {
                      if (window.confirm("Confirmer la suppression ?")) {
                        // Appel API pour supprimer (à implémenter)
                        console.log("Supprimer produit", p.id);
                      }
                    }}
                  >
                    Supprimer
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Products;
