// src/pages/Products.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Products = () => {
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  // Simule un appel API (à adapter selon ton backend)
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // Exemple avec fetch (à adapter à ton endpoint)
        const res = await fetch("/api/products"); // endpoint à définir
        const data = await res.json();

        // Supposons que ton backend renvoie un tableau avec type: "fournisseur" | "interne"
        setProducts(data);
      } catch (err) {
        console.error("Erreur lors du chargement des produits", err);
        alert("Impossible de charger les produits.");
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading) {
    return <div>Chargement des produits...</div>;
  }

  return (
    <div className="products-page">
      <h1>Gestion des produits</h1>
      <p>Liste de tous les produits (fournisseur et internes)</p>

      <button onClick={() => navigate("/add-product")}>
        Ajouter un produit
      </button>

      <table className="products-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nom</th>
            <th>Type</th>
            <th>Prix</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.length === 0 ? (
            <tr>
              <td colSpan="5">Aucun produit trouvé.</td>
            </tr>
          ) : (
            products.map((p) => (
              <tr key={p.id}>
                <td>{p.id}</td>
                <td>{p.name}</td>
                <td>{p.type === "fournisseur" ? "Fournisseur" : "Interne"}</td>
                <td>{p.price} DT</td>
                <td>
                  <button
                    onClick={() => navigate(`/edit-product/${p.id}`)}
                    style={{ marginRight: "8px" }}
                  >
                    Modifier
                  </button>
                  <button
                    onClick={() => {
                      if (window.confirm("Confirmer la suppression ?")) {
                        // Appel API pour supprimer (à implémenter)
                        console.log("Supprimer produit", p.id);
                      }
                    }}
                  >
                    Supprimer
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default Products;
