import React, { useState } from "react";
import "../styles/Login.css"; // Mets ton CSS dans src/pages/Login.css ou src/styles/Login.css

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      if (data.success) {
        localStorage.setItem("token", data.token);
        localStorage.setItem("role", data.user.role);

        if (data.user.role === "ingénieur") {
          window.location.href = "/engineer-dashboard";
        } else if (data.user.role === "responsable_stock") {
          window.location.href = "/stock-dashboard";
        } else {
          alert("Accès refusé : rôle non autorisé");
        }
      } else {
        alert(data.message || "Échec de la connexion");
      }
    } catch (err) {
      console.error("Erreur de connexion:", err);
      alert("Erreur serveur");
    }
  };

  return (
    <div className="main">
      <input type="checkbox" id="chk" aria-hidden="true" />

      {/* Signup */}
      <div className="signup">
        <form>
          <label htmlFor="chk" aria-hidden="true">S'inscrire</label>
          <input type="text" name="txt" placeholder="Nom d'utilisateur" required />
          <input type="email" name="email" placeholder="Email" required />
          <input type="password" name="pswd" placeholder="Mot de passe" required />
          <button type="submit">S'inscrire</button>
        </form>
      </div>

      {/* Login */}
      <div className="login">
        <form onSubmit={handleSubmit}>
          <label htmlFor="chk" aria-hidden="true">Connexion</label>
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Mot de passe"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Se connecter</button>
        </form>
      </div>
    </div>
  );
}

export default Login;
