import React, { useEffect, useState } from 'react';
import { Navigate } from 'react-router-dom';
import "../styles/EngineerDashboard.css";
import { QRCodeSVG } from "qrcode.react";
import BarcodeScannerComponent from "react-qr-barcode-scanner";

function StockManagerDashboard({ user }) {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [scannedProduct, setScannedProduct] = useState(null);
  const [scannerActive, setScannerActive] = useState(true);

  // Charger tous les produits
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/products', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        const data = await res.json();
        setProducts(data.data || []);
      } catch (err) {
        console.error('Erreur chargement produits', err);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  // V√©rification du r√¥le
  if (!user || user.role !== 'responsable_stock') {
    return <Navigate to="/unauthorized" />;
  }

  if (loading) return <p className="loading-text">Chargement...</p>;

  // üî• FONCTION PRINCIPALE : Scan ‚Üí R√©cup√®re produit ‚Üí Affiche boutons
  const handleScan = async (err, result) => {
    if (result) {
      const productId = result.text; // Le QR contient l'ID MongoDB
      console.log("‚úÖ Produit scann√© ID:", productId);
      
      try {
        // R√©cup√©rer le produit complet depuis l'API
        const res = await fetch(`http://localhost:5000/api/products/${productId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
        });
        const data = await res.json();
        
        if (data.success) {
          // ‚úÖ AFFICHE LE PRODUIT AVEC LES BOUTONS DIRECTEMENT
          setScannedProduct(data.data);
        }
      } catch (err) {
        console.error("‚ùå Erreur r√©cup√©ration produit", err);
      }
    }
  };

  // ‚ûï AJOUTER une quantit√©
  const addQuantity = async (productId) => {
    const product = products.find(p => p._id === productId);
    if (!product) return;
    
    const newQuantity = product.quantity + 1;
    
    try {
      const res = await fetch(`http://localhost:5000/api/products/${productId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`
        },
        body: JSON.stringify({ quantity: newQuantity })
      });
      const data = await res.json();
      
      if (data.success) {
        // Mise √† jour du tableau
        setProducts(products.map(p => 
          p._id === productId ? { ...p, quantity: newQuantity } : p
        ));
        // Mise √† jour du produit scann√©
        setScannedProduct({ ...scannedProduct, quantity: newQuantity });
      }
    } catch (err) {
      console.error("‚ùå Erreur ajout quantit√©", err);
    }
  };

  // ‚ûñ RETIRER une quantit√©
  const removeQuantity = async (productId) => {
    const product = products.find(p => p._id === productId);
    if (!product) return;
    if (product.quantity <= 0) {
      alert("‚ö†Ô∏è Stock d√©j√† √† z√©ro");
      return;
    }
    
    const newQuantity = product.quantity - 1;
    
    try {
      const res = await fetch(`http://localhost:5000/api/products/${productId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`
        },
        body: JSON.stringify({ quantity: newQuantity })
      });
      const data = await res.json();
      
      if (data.success) {
        // Mise √† jour du tableau
        setProducts(products.map(p => 
          p._id === productId ? { ...p, quantity: newQuantity } : p
        ));
        // Mise √† jour du produit scann√©
        setScannedProduct({ ...scannedProduct, quantity: newQuantity });
      }
    } catch (err) {
      console.error("‚ùå Erreur retrait quantit√©", err);
    }
  };

  return (
    <div className="dashboard-root">
      <h1>üì¶ Gestion des Stocks</h1>
      
      {/* üéØ SCANNER - Pas d'ID affich√©, pas de redirection */}
      <div className="scanner-section">
        <h2>üì∑ Scanner un produit</h2>
        <div className="scanner-container">
          {scannerActive && (
            <BarcodeScannerComponent
              width={400}
              height={300}
              onUpdate={handleScan}
            />
          )}
          <button 
            className="scanner-toggle"
            onClick={() => setScannerActive(!scannerActive)}
          >
            {scannerActive ? "‚è∏Ô∏è Pause" : "‚ñ∂Ô∏è Activer"}
          </button>
        </div>
      </div>

      {/* üéØ PRODUIT SCANN√â - Boutons Ajouter/Supprimer DIRECTEMENT */}
      {scannedProduct && (
        <div className="scanned-product-panel">
          <h2>üìã Produit scann√©</h2>
          <div className="product-card">
            <div className="product-info">
              <h3>{scannedProduct.name}</h3>
              <p className="product-price">{scannedProduct.price} ‚Ç¨</p>
              <p className="product-desc">{scannedProduct.description || "Aucune description"}</p>
              <div className="stock-display">
                <span className="stock-label">Stock actuel :</span>
                <span className="stock-value">{scannedProduct.quantity}</span>
              </div>
            </div>
            
            {/* ‚úÖ BOUTONS DIRECTEMENT ACCESSIBLES */}
            <div className="action-buttons">
              <button 
                className="btn-add"
                onClick={() => addQuantity(scannedProduct._id)}
              >
                ‚ûï AJOUTER
              </button>
              <button 
                className="btn-remove"
                onClick={() => removeQuantity(scannedProduct._id)}
                disabled={scannedProduct.quantity <= 0}
              >
                ‚ûñ RETIRER
              </button>
            </div>
          </div>
        </div>
      )}

      {/* üìã TABLEAU DES PRODUITS */}
      <h2>üìã Inventaire</h2>
      <table className="product-table">
        <thead>
          <tr>
            <th>Produit</th>
            <th>Prix</th>
            <th>Stock</th>
            <th>QR Code</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p._id}>
              <td>{p.name}</td>
              <td>{p.price} ‚Ç¨</td>
              <td>
                <span className={`stock-badge ${p.quantity <= 5 ? 'low' : ''}`}>
                  {p.quantity}
                </span>
              </td>
              <td>
                <QRCodeSVG value={p._id} size={50} />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* üö™ D√âCONNEXION */}
      <button 
        className="logout-btn"
        onClick={() => {
          localStorage.clear();
          window.location.href = "/";
        }}
      >
        üö™ D√©connexion
      </button>
    </div>
  );
}

export default StockManagerDashboard;
