import React, { useState } from "react";
import "../styles/Login.css";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [remember, setRemember] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const res = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      // üîé DEBUG
      console.log("R√©ponse compl√®te:", data);
      console.log("Role re√ßu:", data.user?.role);

      if (data.success) {
        // Gestion "Se souvenir de moi"
        if (remember) {
          localStorage.setItem("rememberMe", "true");
        } else {
          localStorage.removeItem("rememberMe");
        }

        // Stockage des infos
        localStorage.setItem("token", data.token);
        localStorage.setItem("role", data.user.role);
        localStorage.setItem("userEmail", data.user.email);

        console.log("ROLE STOCK√â LOCAL:", localStorage.getItem("role"));

        // Redirection selon r√¥le
        if (data.user.role === "ing√©nieur" || data.user.role === "ingenieur") {
          window.location.href = "/engineer-dashboard";
        } else if (data.user.role === "responsable_stock") {
          window.location.href = "/stock-dashboard";
        } else {
          alert("Acc√®s refus√© : r√¥le non autoris√©");
          localStorage.clear();
        }
      } else {
        alert(data.message || "√âchec de la connexion");
      }
    } catch (err) {
      console.error("Erreur de connexion:", err);
      alert("Erreur de connexion au serveur. Veuillez r√©essayer.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="container">
      <div className="login-container">
        <div className="circle circle-one"></div>
        <div className="circle circle-two"></div>

        <div className="form-container">
          <h1 className="opacity">Connexion</h1>

          <form onSubmit={handleSubmit}>
            <input
              type="email"
              placeholder="Adresse email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              disabled={loading}
            />

            <input
              type="password"
              placeholder="Mot de passe"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              disabled={loading}
            />

            <button
              className="opacity"
              type="submit"
              disabled={loading}
            >
              {loading ? "Connexion..." : "Se connecter"}
            </button>
          </form>

          <div className="register-forget">
            <label className="opacity">
              <input
                type="checkbox"
                checked={remember}
                onChange={(e) => setRemember(e.target.checked)}
                disabled={loading}
              />
              Se souvenir de moi
            </label>
            <a href="/forgot-password" className="opacity">
              Mot de passe oubli√© ?
            </a>
          </div>
        </div>
      </div>
    </section>
  );
}

export default Login;
