import React, { useState } from "react";
import "./Login.css"; // Assure-toi que ton CSS est bien importé

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [remember, setRemember] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch("http://localhost:5000/api/auth/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ email, password }),
      });

      const data = await res.json();

      if (data.success) {
        localStorage.setItem("token", data.token);
        localStorage.setItem("role", data.user.role);

        if (data.user.role === "ingénieur") {
          window.location.href = "/engineer-dashboard";
        } else if (data.user.role === "responsable_stock") {
          window.location.href = "/stock-dashboard";
        } else {
          alert("Accès refusé : rôle non autorisé");
        }
      } else {
        alert(data.message || "Échec de la connexion");
      }
    } catch (err) {
      console.error("Erreur de connexion:", err);
      alert("Erreur serveur");
    }
  };

  return (
    <section className="container">
      <div className="login-container">
        <div className="circle circle-one"></div>

        <div className="form-container">
          <img
            src="https://raw.githubusercontent.com/hicodersofficial/glassmorphism-login-form/master/assets/illustration.png"
            alt="illustration"
            className="illustration"
          />
          <h1 className="opacity">Connexion</h1>

          <form onSubmit={handleSubmit}>
            <input
              type="email"
              placeholder="Adresse email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />

            <input
              type="password"
              placeholder="Mot de passe"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />

            <button className="opacity" type="submit">
              Se connecter
            </button>
          </form>

          <div className="register-forget opacity">
            <label>
              <input
                type="checkbox"
                checked={remember}
                onChange={(e) => setRemember(e.target.checked)}
              />
              Se souvenir de moi
            </label>
            <a href="#">Mot de passe oublié ?</a>
          </div>
        </div>

        <div className="circle circle-two"></div>
      </div>
      <div className="theme-btn-container"></div>
    </section>
  );
}

export default Login;
