import React, { useEffect, useState, useCallback } from 'react';
import { Navigate } from 'react-router-dom';
import "../styles/EngineerDashboard.css";
import { QRCodeSVG } from "qrcode.react";
import BarcodeScannerComponent from "react-qr-barcode-scanner";

function StockManagerDashboard({ user }) {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [scannedProduct, setScannedProduct] = useState(null);
  const [isScanning, setIsScanning] = useState(true);

  // Charger tous les produits
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/products', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        const data = await res.json();
        setProducts(data.data || []);
      } catch (err) {
        console.error('Erreur lors du chargement des produits', err);
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  // R√©cup√©rer un produit scann√© par son ID
  const fetchProductById = useCallback(async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/products/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` }
      });
      const data = await res.json();
      if (data.success) {
        setScannedProduct(data.data);
      }
    } catch (err) {
      console.error("Erreur r√©cup√©ration produit scann√©", err);
    }
  }, []);

  // V√©rification du r√¥le
  if (!user || user.role !== 'responsable_stock') {
    return <Navigate to="/unauthorized" />;
  }

  if (loading) return <p className="loading-text">Chargement...</p>;

  // Fonction pour mettre √† jour la quantit√©
  const updateQuantity = async (productId, action) => {
    const product = products.find(p => p._id === productId);
    if (!product) return;

    const newQuantity = action === "add" ? product.quantity + 1 : product.quantity - 1;
    
    if (newQuantity < 0) {
      alert("La quantit√© ne peut pas √™tre n√©gative");
      return;
    }

    try {
      const res = await fetch(`http://localhost:5000/api/products/${productId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`
        },
        body: JSON.stringify({ quantity: newQuantity })
      });
      const data = await res.json();
      if (data.success) {
        // Mettre √† jour la liste des produits
        setProducts(products.map(p => p._id === productId ? { ...p, quantity: newQuantity } : p));
        // Mettre √† jour le produit scann√©
        setScannedProduct(prev => ({ ...prev, quantity: newQuantity }));
      }
    } catch (err) {
      console.error("Erreur mise √† jour quantit√©", err);
    }
  };

  // Scanner en continu sans redirection ni affichage d'ID brut
  const handleScan = (err, result) => {
    if (result) {
      const scannedId = result.text;
      // V√©rifier si c'est un ID valide (24 caract√®res hexad√©cimaux pour MongoDB)
      if (scannedId && scannedId.length === 24 && /^[0-9a-fA-F]+$/.test(scannedId)) {
        fetchProductById(scannedId);
      }
    }
    if (err) {
      console.error("Erreur de scan:", err);
    }
  };

  return (
    <div className="dashboard-root">
      <h1>üì¶ Dashboard Responsable Stock</h1>
      <h2>Liste des produits et quantit√©s disponibles</h2>

      <table className="product-table">
        <thead>
          <tr>
            <th>Nom</th>
            <th>Description</th>
            <th>Prix</th>
            <th>Quantit√© disponible</th>
            <th>QR Code</th>
          </tr>
        </thead>
        <tbody>
          {products.map(p => (
            <tr key={p._id}>
              <td>{p.name}</td>
              <td>{p.description || "‚Äî"}</td>
              <td>{p.price} ‚Ç¨</td>
              <td>{p.quantity}</td>
              <td>
                <QRCodeSVG 
                  value={p._id} 
                  size={64}
                  bgColor="#ffffff"
                  fgColor="#000000"
                  level="L"
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="scanner-section">
        <h2>üì∑ Scanner un QR code produit</h2>
        <div className="scanner-container">
          {isScanning && (
            <BarcodeScannerComponent
              width={400}
              height={300}
              onUpdate={handleScan}
            />
          )}
          <button 
            className="toggle-scanner-btn"
            onClick={() => setIsScanning(!isScanning)}
          >
            {isScanning ? "‚è∏Ô∏è Pause scanner" : "‚ñ∂Ô∏è Reprendre scanner"}
          </button>
        </div>
      </div>

      {scannedProduct && (
        <div className="product-actions">
          <h3>üìã Produit scann√©</h3>
          <div className="scanned-product-card">
            <h4>{scannedProduct.name}</h4>
            <p><strong>R√©f√©rence:</strong> {scannedProduct._id.substring(0, 8)}...</p>
            <p><strong>Quantit√© actuelle:</strong> {scannedProduct.quantity}</p>
            <p><strong>Prix:</strong> {scannedProduct.price} ‚Ç¨</p>
            <div className="action-buttons">
              <button 
                className="btn-add"
                onClick={() => updateQuantity(scannedProduct._id, "add")}
              >
                ‚ûï Ajouter ( +1 )
              </button>
              <button 
                className="btn-remove"
                onClick={() => updateQuantity(scannedProduct._id, "remove")}
                disabled={scannedProduct.quantity <= 0}
              >
                ‚ûñ Supprimer ( -1 )
              </button>
            </div>
          </div>
        </div>
      )}

      <div className="dashboard-footer">
        <button 
          className="logout-btn"
          onClick={() => {
            localStorage.clear();
            window.location.href = "/";
          }}
        >
          üö™ D√©connexion
        </button>
      </div>
    </div>
  );
}

export default StockManagerDashboard;
